import React, { Component } from 'react';
import Itemlist from './item_list';

//function for choosing items

const list_of_items = ["공기청정기","청소기","캠슐커피머신","바람막이","홍삼"];
const cost_of_items = [400000, 800000, 200000, 100000, 50000];

function choose_items(){
    //item 3가지 뽑기
    const item1 = Math.floor(Math.random() * 5 + 1);
    const item2 = item1;
    while (item2===item1){
        item2 = Math.floor(Math.random() * 5 + 1);
    }
    const item3 = item1;
    while (item3===item1||item3===item2){
        item3 = Math.floor(Math.random() * 5 + 1);
    }
    // 각각의 상승률 정하기
    return(
        <div>
            <Itemlist name={list_of_items[item1]} cost = {cost_of_items[item1]}/>
            <Itemlist name="커피머신" cost = "20"/>
            <Itemlist name="컴퓨터" cost = ""/>
        </div>
    )


}

class Marketview extends Component {
    render(){
        return (
            <div>
                <Itemlist name="컴퓨터" cost = "100만원"/>
                <Itemlist name="커피머신" cost = "20만원"/>
            </div>
            
        );
    }
    
}

export default Marketview;
